<script>
    $(document).ready(function() {
        // Thực hiện yêu cầu AJAX để lấy dữ liệu từ máy chủ
        $.ajax({
            url: 'path/to/your/api',  // URL của API để lấy dữ liệu
            method: 'GET',
            dataType: 'json',
            success: function(response) {
                // Giả sử dữ liệu trả về có dạng: { labels: ['1', '2', '3', '4', '5'], data: [10, 20, 30, 40, 50] }
                var labels = response.labels;
                var data = response.data;

                var ctx = $('#myChart')[0].getContext('2d');
                var myChart = new Chart(ctx, {
                    type: 'bar',
                    data: {
                        labels: labels,
                        datasets: [{
                            label: 'Tỷ lệ %',
                            data: data,
                            backgroundColor: [
                                'rgba(255, 99, 132, 0.2)',
                                'rgba(54, 162, 235, 0.2)',
                                'rgba(255, 206, 86, 0.2)',
                                'rgba(75, 192, 192, 0.2)',
                                'rgba(153, 102, 255, 0.2)'
                            ],
                            borderColor: [
                                'rgba(255, 99, 132, 1)',
                                'rgba(54, 162, 235, 1)',
                                'rgba(255, 206, 86, 1)',
                                'rgba(75, 192, 192, 1)',
                                'rgba(153, 102, 255, 1)'
                            ],
                            borderWidth: 1
                        }]
                    },
                    options: {
                        plugins: {
                            datalabels: {
                                anchor: 'end',
                                align: 'end',
                                formatter: function(value) {
                                    return value + '%';
                                },
                                color: 'black'
                            }
                        },
                        scales: {
                            x: {
                                grid: {
                                    display: false
                                }
                            },
                            y: {
                                beginAtZero: true,
                                ticks: {
                                    callback: function(value) {
                                        return value + '%';  // Thêm ký hiệu % vào giá trị trên trục y
                                    }
                                },
                                suggestedMax: 100,  // Giới hạn tối đa trên trục y là 100%
                                grid: {
                                    display: false
                                }
                            }
                        },
                        onClick: function(event, elements) {
                            if (elements.length > 0) {
                                var elementIndex = elements[0].index;
                                var value = this.data.datasets[0].data[elementIndex];
                                alert('Tỷ lệ: ' + value + '%');
                            }
                        }
                    },
                    plugins: [ChartDataLabels]
                });
            },
            error: function(error) {
                console.error('Lỗi khi lấy dữ liệu từ máy chủ:', error);
            }
        });
    });
</script>
